from socket import *
from random import *
from rsa import RSA

client_address = ("172.20.10.7", 12500)
clientSocket = socket(AF_INET, SOCK_DGRAM) # AF_INET6
rsa = RSA()
print("UDP Client\n")

def main():

    public_key = (65537, )
    plaintext = "The information security is of significant importance to ensure the privacy of communications"
    
    encrypted_message = RSA.encrypt(public_key, plaintext)
    encrypted_data = ','.join(map(str, encrypted_message)).encode('utf-8')
    print(plaintext)
   
    clientSocket.sendto(encrypted_data, client_address)
    clientSocket.close()

